import React, { useState } from "react";
import './styles.css';

function App() {
  const [interest, setInterest] = useState("Fashion and Styling");
  const [category, setCategory] = useState(1);
  const [personality, setPersonality] = useState("");
  const [relationship, setRelationship] = useState("");
  const [recommendations, setRecommendations] = useState([]);

  // Simulated gift recommendations based on user input
  const sampleRecommendations = {
    "Fashion and Styling": ["Adidas Originals Shorts", "A Black Dress", "Leather Jacket"],
    "Books": ["The Little Prince", "A Brief History of Time"],
    "Music": ["A Vinyl Record", "Concert Tickets"]
  };

  const getRecommendations = () => {
    const result = sampleRecommendations[interest] || [];
    setRecommendations(result);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    getRecommendations();
  };

  return (
    <div className="app">
      {/* Heart SVG Shape */}
      <svg xmlns="http://www.w3.org/2000/svg">
        <clipPath id="heart-shape">
          <path d="M50,80 Q60,70 70,60 Q80,50 90,40 Q95,35 100,30 Q105,25 105,20 Q105,10 95,10 Q85,10 75,20 Q65,30 50,50 Q35,30 25,20 Q15,10 5,10 Q-5,10 -5,20 Q-5,25 0,30 Q5,35 10,40 Q20,50 30,60 Q40,70 50,80 Z" />
        </clipPath>
      </svg>

      {/* Main Content */}
      <div className="content">
        <h1>Valentine's Gift Recommendation</h1>
        <p>Choose your preferences:</p>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="interest">Interest:</label>
            <select id="interest" value={interest} onChange={(e) => setInterest(e.target.value)}>
              <option value="Fashion and Styling">Fashion and Styling</option>
              <option value="Books">Books</option>
              <option value="Music">Music</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="category">Category:</label>
            <select id="category" value={category} onChange={(e) => setCategory(Number(e.target.value))}>
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
              <option value="4">4</option>
              <option value="5">5</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="personality">Personality:</label>
            <input
              type="text"
              id="personality"
              value={personality}
              onChange={(e) => setPersonality(e.target.value)}
              placeholder="e.g. adventurous"
            />
          </div>

          <div className="form-group">
            <label htmlFor="relationship">Relationship Type:</label>
            <input
              type="text"
              id="relationship"
              value={relationship}
              onChange={(e) => setRelationship(e.target.value)}
              placeholder="e.g. extravagant"
            />
          </div>

          <button type="submit">Get Recommendations</button>
        </form>

        {/* Recommended Gifts */}
        <div className="recommendations">
          {recommendations.length > 0 ? (
            <>
              <h3>Recommended Gifts:</h3>
              {recommendations.map((gift, index) => (
                <p key={index}>{gift}</p>
              ))}
            </>
          ) : (
            <p>No gifts found for the given preferences.</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
