import React, { useState } from "react"; 
import Favicon from "react-favicon";
import "./styles.css";

function App() {
    const heartFavicon = "/love.svg"; // Set favicon
    const [step, setStep] = useState(1); // Track the current step
    const [category, setCategory] = useState("");
    const [interest, setInterest] = useState("");
    const [personality, setPersonality] = useState("");
    const [relationshipType, setRelationshipType] = useState("");
    const [recommendation, setRecommendation] = useState([]);

    const handleNextStep = async () => {
        try {
            console.log("Button clicked");
            if (step === 4) {
                console.log("Submitting request to backend...");

                const response = await fetch("http://127.0.0.1:5000/recommend", {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                    body: JSON.stringify({
                        category,
                        interest,
                        personality,
                        relationshipType,
                    }),
                });

                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }

                const data = await response.json();
                console.log("Recommended gifts:", data.recommendation);

                // Ensure recommendation is an array
                setRecommendation(data.recommendation || []);

            } else {
                setStep(step + 1);
            }
        } catch (error) {
            console.error("Error with fetch:", error);
            setRecommendation([]);  // Prevent errors in UI by setting an empty array
        }
    };



    const renderFormStep = () => {
        switch (step) {
            case 1:
                return (
                    <div>
                        <label>Budget Category:</label>
                        <select value={category} onChange={(e) => setCategory(e.target.value)}>
                            <option value="">Select</option>
                            <option value="1">0-500</option>
                            <option value="2">500-1000</option>
                            <option value="3">1000-2000</option>
                            <option value="4">2000-5000</option>
                            <option value="5">5000+</option>
                        </select>
                    </div>
                );
            case 2:
                return (
                    <div>
                        <label>What interests her?</label>
                        <select value={interest} onChange={(e) => setInterest(e.target.value)}>
                            <option value="">Select</option>
                            <option value="Books">Books</option>
                            <option value="Music">Music</option>
                            <option value="Fashion & Styling">Fashion & Styling</option>
                            <option value="Art & DIY">Art & DIY</option>
                            <option value="Home Décor">Home Décor</option>
                        </select>
                    </div>
                );
            case 3:
                return (
                    <div>
                        <label>What is she like (personality and style)?</label>
                        <select value={personality} onChange={(e) => setPersonality(e.target.value)}>
                            <option value="">Select</option>
                            <option value="quirky">Quirky</option>
                            <option value="simple">Simple</option>
                            <option value="bold">Bold</option>
                            <option value="artistic">Artistic</option>
                            <option value="adventurous">Adventurous</option>
                            <option value="soft&feminine">Soft & Feminine</option>
                            <option value="vibrant">Vibrant</option>
                            <option value="funny">Funny</option>
                        </select>
                    </div>
                );
            case 4:
                return (
                    <div>
                        <label>Relationship type:</label>
                        <select value={relationshipType} onChange={(e) => setRelationshipType(e.target.value)}>
                            <option value="">Select</option>
                            <option value="sentimental">Sentimental (Loves personalized gifts)</option>
                            <option value="practical">Practical (Loves useful gifts)</option>
                            <option value="low-key">Low-key (Doesn’t like extravagant gifts)</option>
                            <option value="extravagant">Extravagant (Loves big surprises, expensive gifts)</option>
                        </select>
                    </div>
                );
            default:
                return null;
        }
    };

    return (
        <div className="app">
            <Favicon url={heartFavicon} />
            <div className="content">
                <h1>Valentine's Gift Recommendation</h1>
                <form>
                    {renderFormStep()}
                    <button type="button" onClick={handleNextStep}>
                        {step === 4 ? "Get Recommendation" : "Next"}
                    </button>
                </form>
                {recommendation.length > 0 && (
                    <div>
                        <h3>Recommended Gifts:</h3>
                        <ul>
                            {recommendation.map((gift, index) => (
                                <li key={index}>{gift}</li>
                            ))}
                        </ul>
                    </div>
                )}
            </div>
        </div>
    );
}

export default App;
