import React from "react";
import './styles.css';
import Favicon from "react-favicon";

function App() {
  const heartFavicon = "love.svg"; // Heart favicon in public folder

  // Generate floating hearts dynamically
  const generateHearts = () => {
    const heartsArray = [];
    for (let i = 0; i < 20; i++) {
      const size = Math.random() * 20 + 20; // Random size between 20px - 40px
      const left = Math.random() * 100; // Random position
      const duration = Math.random() * 5 + 5; // Different speeds

      heartsArray.push(
        <div
          key={i}
          className="heart"
          style={{
            left: `${left}%`,
            width: `${size}px`,
            height: `${size}px`,
            animationDuration: `${duration}s`
          }}
        ></div>
      );
    }
    return heartsArray;
  };

  return (
    <div className="app">
      <Favicon url={heartFavicon} />
      <div className="hearts-container">{generateHearts()}</div>

      {/* Form Content */}
      <div className="content">
        <h1>Valentine's Gift Recommendation</h1>
        <p>Choose your preferences:</p>
      </div>
    </div>
  );
}

export default App;
