import React, { useState } from "react";
import Favicon from "react-favicon";
import "./styles.css";

function App() {
    const heartFavicon = "/love.svg";
    const [step, setStep] = useState(1);
    const [category, setCategory] = useState("");
    const [interest, setInterest] = useState("");
    const [personality, setPersonality] = useState("");
    const [relationshipType, setRelationshipType] = useState("");
    const [recommendation, setRecommendation] = useState([]);

    const handleNextStep = async () => {
        try {
            console.log("Button clicked");
            if (step === 4) {
                console.log("Submitting request to backend...");

                const response = await fetch("http://127.0.0.1:5000/recommend", {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                    body: JSON.stringify({
                        category,
                        interest,
                        personality,
                        relationshipType,
                    }),
                });

                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }

                const data = await response.json();
                console.log("Recommended gifts:", data.recommendation);

                setRecommendation(data.recommendation || []);
            } else {
                setStep(step + 1);
            }
        } catch (error) {
            console.error("Error with fetch:", error);
            setRecommendation([]);
        }
    };

    const renderFormStep = () => {
        switch (step) {
            case 1:
                return (
                    <div>
                        <label>üí∞ Budget Category:</label>
                        {["0-500", "500-1000", "1000-2000", "2000-5000", "5000+"].map((value, index) => (
                            <div key={index}>
                                <input
                                    type="radio"
                                    name="category"
                                    value={index + 1}
                                    checked={category === (index + 1).toString()}
                                    onChange={(e) => setCategory(e.target.value)}
                                />
                                <label>{value}</label>
                            </div>
                        ))}
                    </div>
                );
            case 2:
                return (
                    <div>
                        <label>üéÅ What interests her?</label>
                        {["Books", "Music", "Fashion & Styling", "Art & DIY", "Home D√©cor", "Self Care (Nykaa Sale)"].map((value, index) => (
                            <div key={index}>
                                <input
                                    type="radio"
                                    name="interest"
                                    value={value}
                                    checked={interest === value}
                                    onChange={(e) => setInterest(e.target.value)}
                                />
                                <label>{value}</label>
                            </div>
                        ))}
                    </div>
                );
            case 3:
                return (
                    <div>
                        <label>‚ú® What is she like (personality and style)?</label>
                        {["Quirky", "Simple", "Bold", "Artistic", "Adventurous", "Soft & Feminine", "Vibrant", "Funny"].map((value, index) => (
                            <div key={index}>
                                <input
                                    type="radio"
                                    name="personality"
                                    value={value}
                                    checked={personality === value}
                                    onChange={(e) => setPersonality(e.target.value)}
                                />
                                <label>{value}</label>
                            </div>
                        ))}
                    </div>
                );
            case 4:
                return (
                    <div>
                        <label>üíû Relationship type:</label>
                        {["Sentimental (Loves personalized gifts)", "Practical (Loves useful gifts)", "Low-key (Doesn‚Äôt like extravagant gifts)", "Extravagant (Loves big surprises, expensive gifts)"].map((value, index) => (
                            <div key={index}>
                                <input
                                    type="radio"
                                    name="relationshipType"
                                    value={value}
                                    checked={relationshipType === value}
                                    onChange={(e) => setRelationshipType(e.target.value)}
                                />
                                <label>{value}</label>
                            </div>
                        ))}
                    </div>
                );
            default:
                return null;
        }
    };

    return (
        <div className="app">
            <Favicon url={heartFavicon} />
            <div className="content">
                <h1>üíù Valentine's Gift Recommendation</h1>
                <form>
                    {renderFormStep()}
                    <button type="button" onClick={handleNextStep}>
                        {step === 4 ? "üéÅ Get Recommendation" : "‚û°Ô∏è Next"}
                    </button>
                </form>

                {recommendation && recommendation.length > 0 && (
                    <div>
                        <h3>üéÄ Recommended Gifts:</h3>
                        <ul>
                            {recommendation.map((gift, index) => (
                                <li key={index}>‚ú® {gift}</li>
                            ))}
                        </ul>
                    </div>
                )}
            </div>
        </div>
    );
}

export default App;
