import React from "react";
import Favicon from "react-favicon";
import "./styles.css";

function App() {
    const heartFavicon = "/love.svg"; // Set favicon

    // Function to generate floating hearts dynamically
    const generateHearts = () => {
        return Array.from({ length: 15 }).map((_, i) => {
            const size = Math.random() * 30 + 20; // Random size 20px - 50px
            const left = Math.random() * 100; // Random horizontal position
            const duration = Math.random() * 5 + 3; // Different speeds

            return (
                <div
                    key={i}
                    className="heart"
                    style={{
                        left: `${left}%`,
                        width: `${size}px`,
                        height: `${size}px`,
                        animationDuration: `${duration}s`
                    }}
                ></div>
            );
        });
    };

    return (
        <div className="app">
            <Favicon url={heartFavicon} />
            <div className="hearts-container">{generateHearts()}</div>

            {/* Centered Content */}
            <div className="content">
                <h1>Valentine's Gift Recommendation</h1>
                <p>Choose your preferences:</p>
            </div>
        </div>
    );
}

export default App;
