import React, { useState } from "react";
import Favicon from "react-favicon";
import "./styles.css";

function App() {
    const heartFavicon = "/love.svg";
    const linkedinLogo = "/social.png";  // Direct path from the public folder
    const instagramLogo = "/instagram.png"; // Direct path from the public folder
    const [step, setStep] = useState(0);
    const [category, setCategory] = useState("");
    const [interest, setInterest] = useState("");
    const [personality, setPersonality] = useState("");
    const [relationshipType, setRelationshipType] = useState("");
    const [recommendation, setRecommendation] = useState([]);
    const [warning, setWarning] = useState(""); // ‚úÖ State to handle warnings

    const handleNextStep = async () => {
        // ‚úÖ Check if the user has selected an option before proceeding
        if ((step === 1 && !category) ||
            (step === 2 && !interest) ||
            (step === 3 && !personality) ||
            (step === 4 && !relationshipType)) {
            setWarning("‚ö†Ô∏è Please choose one!");
            return;
        }

        setWarning(""); // ‚úÖ Clear warning if an option is selected

        if (step === 4) {
            try {
                console.log("Submitting request to backend...");

                const response = await fetch("http://127.0.0.1:5000/recommend", {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                    body: JSON.stringify({
                        category,
                        interest,
                        personality,
                        relationshipType,
                    }),
                });

                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }

                const data = await response.json();
                console.log("Recommended gifts:", data.recommendation);
                setRecommendation(data.recommendation || []);
            } catch (error) {
                console.error("Error with fetch:", error);
                setRecommendation([]);  // Prevent errors in UI by setting an empty array
            }
        } else {
            setStep(step + 1);
        }
    };

    const renderFormStep = () => {
        switch (step) {
            case 0:
                return (
                    <div className="intro">
                        <h1>Let me help you find a gift for your girl, you lazy bums.</h1>
                        <p>Finding the perfect gift is hard, I get it. But don't worry, I got you.</p>
                        <button type="button" onClick={() => setStep(1)}>Let's start</button>
                    </div>
                );
            case 1:
                return (
                    <div>
                        <h2>$ Budget Category:</h2>
                        {["0-500", "500-1000", "1000-2000", "2000-5000", "5000+"].map((value, index) => (
                            <div key={index}>
                                <input
                                    type="radio"
                                    name="category"
                                    value={index + 1}
                                    checked={category === (index + 1).toString()}
                                    onChange={(e) => setCategory(e.target.value)}
                                />
                                <label>{value}</label>
                            </div>
                        ))}
                    </div>
                );
            case 2:
                return (
                    <div>
                        <h2>üéÅ What interests her?</h2>
                        {[
                            { label: "Books", value: "Books" },
                            { label: "Music", value: "Music" },
                            { label: "Fashion & Styling", value: "Fashion & Styling" },
                            { label: "Art & DIY", value: "Art & DIY" },
                            { label: "Home D√©cor", value: "Home D√©cor" },
                            { label: "Self Care (Nykaa Sale)", value: "Self Care" },
                        ].map((item, index) => (
                            <div key={index}>
                                <input
                                    type="radio"
                                    name="interest"
                                    value={item.value}
                                    checked={interest === item.value}
                                    onChange={(e) => setInterest(e.target.value)}
                                />
                                <label>{item.label}</label>
                            </div>
                        ))}
                    </div>
                );
            case 3:
                return (
                    <div>
                        <h2>‚ú® What is she like (personality and style)?</h2>
                        {["Quirky", "Simple", "Bold", "Artistic", "Adventurous", "Soft & Feminine", "Vibrant", "Funny"].map((value, index) => (
                            <div key={index}>
                                <input
                                    type="radio"
                                    name="personality"
                                    value={value}
                                    checked={personality === value}
                                    onChange={(e) => setPersonality(e.target.value)}
                                />
                                <label>{value}</label>
                            </div>
                        ))}
                    </div>
                );
            case 4:
                return (
                    <div>
                        <h2>üíû Relationship type:</h2>
                        {["Sentimental (Loves personalized gifts)", "Practical (Loves useful gifts)", "Low-key (Doesn‚Äôt like extravagant gifts)", "Extravagant (Loves big surprises, expensive gifts)"].map((value, index) => (
                            <div key={index}>
                                <input
                                    type="radio"
                                    name="relationshipType"
                                    value={value}
                                    checked={relationshipType === value}
                                    onChange={(e) => setRelationshipType(e.target.value)}
                                />
                                <label>{value}</label>
                            </div>
                        ))}
                    </div>
                );
            default:
                return null;
        }
    };

    return (
        <div className="app">
            <Favicon url={heartFavicon} />
            <div className="navbar">
                <h1>Lazy Gifting.</h1>
            </div>
            <div className="content">
                {renderFormStep()}

                {/* ‚úÖ Show warning if nothing is selected */}
                {warning && <p className="warning">{warning}</p>}

                {step > 0 && (
                    <button type="button" onClick={handleNextStep}>
                        {step === 4 ? "üéÅ Get Recommendation" : "Next ‚Üí"}
                    </button>
                )}

                {recommendation && recommendation.length > 0 && (
                    <div>
                        <h3>Recommended Gifts:</h3>
                        <ul>
                            {recommendation.map((gift, index) => (
                                <li key={index}>‚ú® {gift}</li>
                            ))}
                        </ul>
                    </div>
                )}
            </div>

            <footer className="footer">
                <p className="footer-text">
                    Made with love for love by Neha <span className="heart">‚ù§Ô∏è</span>
                </p>
                <div className="social-icons">
                    <a href="https://www.linkedin.com/in/i-am-neha-singh/" target="_blank" rel="noopener noreferrer">
                        <img src={linkedinLogo} alt="LinkedIn" />
                    </a>
                    <a href="https://www.instagram.com/nehasingh.py/" target="_blank" rel="noopener noreferrer">
                        <img src={instagramLogo} alt="Instagram" />
                    </a>
                </div>
            </footer>
        </div>
    );
}

export default App;
