import React, { useState } from "react";
import Favicon from "react-favicon";
import "./styles.css";

function App() {
    const heartFavicon = "/love.svg";
    const [step, setStep] = useState(0); // Start with an intro step
    const [category, setCategory] = useState("");
    const [interest, setInterest] = useState("");
    const [personality, setPersonality] = useState("");
    const [relationshipType, setRelationshipType] = useState("");
    const [recommendation, setRecommendation] = useState([]);
    const [showRecommendations, setShowRecommendations] = useState(false); // State to toggle recommendations

    const handleNextStep = async () => {
        try {
            console.log("Button clicked");
            if (step === 4) {
                console.log("Submitting request to backend...");

                const response = await fetch("http://127.0.0.1:5000/recommend", {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                    body: JSON.stringify({
                        category,
                        interest,
                        personality,
                        relationshipType,
                    }),
                });

                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }

                const data = await response.json();
                console.log("Recommended gifts:", data.recommendation);

                setRecommendation(data.recommendation || []);
                setShowRecommendations(true);  // Show recommendations after form submission
            } else {
                setStep(step + 1);
            }
        } catch (error) {
            console.error("Error with fetch:", error);
            setRecommendation([]);
        }
    };

    const renderFormStep = () => {
        switch (step) {
            case 0:
                return (
                    <div className="intro">
                        <h1>Let me help you find a gift for your girl, you lazy bums. 🎁</h1>
                        <p>Finding the perfect gift is hard, I get it. But don't worry, I got you.</p>
                        <button type="button" onClick={() => setStep(1)}>
                            Let's start 🚀
                        </button>
                    </div>
                );
            case 1:
                return (
                    <div>
                        <h2>💰 Budget Category:</h2>
                        {["0-500", "500-1000", "1000-2000", "2000-5000", "5000+"].map((value, index) => (
                            <div key={index}>
                                <input
                                    type="radio"
                                    name="category"
                                    value={index + 1}
                                    checked={category === (index + 1).toString()}
                                    onChange={(e) => setCategory(e.target.value)}
                                />
                                <label>{value}</label>
                            </div>
                        ))}
                    </div>
                );
            case 2:
                return (
                    <div>
                        <h2>🎁 What interests her?</h2>
                        {[
                            { label: "Books", value: "Books" },
                            { label: "Music", value: "Music" },
                            { label: "Fashion & Styling", value: "Fashion & Styling" },
                            { label: "Art & DIY", value: "Art & DIY" },
                            { label: "Home Décor", value: "Home Décor" },
                            { label: "Self Care (Nykaa Sale)", value: "Self Care" } // ✅ Now it sends "Self Care"
                        ].map((item, index) => (
                            <div key={index}>
                                <input
                                    type="radio"
                                    name="interest"
                                    value={item.value} // ✅ Backend receives "Self Care" instead of "Self Care (Nykaa Sale)"
                                    checked={interest === item.value}
                                    onChange={(e) => setInterest(e.target.value)}
                                />
                                <label>{item.label}</label>
                            </div>
                        ))}
                    </div>
                );
            case 3:
                return (
                    <div>
                        <h2>✨ What is she like (personality and style)?</h2>
                        {["Quirky", "Simple", "Bold", "Artistic", "Adventurous", "Soft & Feminine", "Vibrant", "Funny"].map((value, index) => (
                            <div key={index}>
                                <input
                                    type="radio"
                                    name="personality"
                                    value={value}
                                    checked={personality === value}
                                    onChange={(e) => setPersonality(e.target.value)}
                                />
                                <label>{value}</label>
                            </div>
                        ))}
                    </div>
                );
            case 4:
                return (
                    <div>
                        <h2>💞 Relationship type:</h2>
                        {["Sentimental (Loves personalized gifts)", "Practical (Loves useful gifts)", "Low-key (Doesn’t like extravagant gifts)", "Extravagant (Loves big surprises, expensive gifts)"].map((value, index) => (
                            <div key={index}>
                                <input
                                    type="radio"
                                    name="relationshipType"
                                    value={value}
                                    checked={relationshipType === value}
                                    onChange={(e) => setRelationshipType(e.target.value)}
                                />
                                <label>{value}</label>
                            </div>
                        ))}
                    </div>
                );
            default:
                return null;
        }
    };

    return (
        <div className="app">
            <Favicon url={heartFavicon} />
            <header className="navbar">
                <h1>Lazy Gifting (for her)</h1>
            </header>
            <div className="content">
                {showRecommendations ? (
                    <div>
                        <h3>🎁 Let me surprise you with some thoughtful ideas... ✨</h3>
                        <div className="gift-list">
                            {recommendation.map((gift, index) => (
                                <div key={index} className="gift-item">{gift}</div>
                            ))}
                        </div>
                    </div>
                ) : (
                    <>
                        {renderFormStep()}
                        {step > 0 && (
                            <button type="button" onClick={handleNextStep}>
                                {step === 4 ? "🎁 Get Recommendation" : "➡️ Next"}
                            </button>
                        )}
                    </>
                )}
            </div>
        </div>
    );
}

export default App;
